	@Test 
	public void testGetPriceDetailsForProduct() {
		
		boolean isCreditCheckRequired = false;
		SalesOrderSimulationRequestData requestData = salesOrderSimulationService.getPriceStockRequestData(productModel, isCreditCheckRequired);
		requestData.setItems(salesOrderSimulationService.getItemLevelDetails(productModel));
		try {
			when(mapper.writeValueAsString(requestData)).thenReturn(PAYLOAD);
			when (salesOrderSimulationOutboundRequest
					.getResponseFromSalesOrderSimulate(PAYLOAD)).thenReturn(
							JSON_RESPONSE);
			List<PriceInformation> priceInfo = salesOrderSimulationService.getPriceDetailsForProduct(productModel);
			assertFalse(priceInfo.isEmpty());
			Assert.assertThat(Double.valueOf(PRICE_1), is(priceInfo.iterator().next().getPriceValue().getValue()));
		} catch (JsonProcessingException e) {
			LOGGER.error(e);
			fail(UNABLE_TO_PARSE_THE_JSON_BODY_PAYLOAD);
		}
	}
	
	
