	@Test
	public void testCheckCreditLimitExceeded() {
		
		
		boolean isCreditCheckRequired = true;
		SalesOrderSimulationRequestData requestData = salesOrderSimulationService.getPriceStockRequestData(cartModel, isCreditCheckRequired);
		requestData.setItems(salesOrderSimulationService.getItemLevelDetails(cartModel));
		try {
			when(mapper.writeValueAsString(requestData)).thenReturn(CREDIT_CHECK_PAYLOAD);
			when (salesOrderSimulationOutboundRequest
					.getResponseFromSalesOrderSimulate(CREDIT_CHECK_PAYLOAD)).thenReturn(
							CREDIT_LIMIT_FAILURE_JSON);
			boolean creditLimitExceeded = salesOrderSimulationService.checkCreditLimitExceeded(cartModel,user);
			Assert.assertThat(true, is(creditLimitExceeded));
			when (salesOrderSimulationOutboundRequest
					.getResponseFromSalesOrderSimulate(CREDIT_CHECK_PAYLOAD)).thenReturn(
							CREDIT_LIMIT_SUCCESS_JSON);
			creditLimitExceeded = salesOrderSimulationService.checkCreditLimitExceeded(cartModel,user);
			Assert.assertThat(false, is(creditLimitExceeded));
			
			
		} catch (JsonProcessingException e) {
			LOGGER.error(e);
			fail(UNABLE_TO_PARSE_THE_JSON_BODY_PAYLOAD);
		}
		
	}
	
	
